class Solution {
    public int solution(int[] nums) {
        // Bug: Initialization to 0 fails for all negative numbers
        int maxSoFar = 0;
        int currentMax = 0;
        for (int x : nums) {
            currentMax = Math.max(x, currentMax + x);
            maxSoFar = Math.max(maxSoFar, currentMax);
        }
        return maxSoFar;
    }
}
